package com.config;import org.mybatis.generator.api.MyBatisGenerator;import org.mybatis.generator.config.Configuration;import org.mybatis.generator.config.xml.ConfigurationParser;import org.mybatis.generator.internal.DefaultShellCallback;import org.springframework.core.io.ClassPathResource;import java.io.File;import java.util.ArrayList;import java.util.List;public class Test {    public static void main(String[] args) throws  Exception {        File configFile = new ClassPathResource("generator/generatorConfig.xml").getFile();        List<String> warnings = new ArrayList<String>();        ConfigurationParser cp = new ConfigurationParser(warnings);        Configuration config = cp.parseConfiguration(configFile);        DefaultShellCallback callback = new DefaultShellCallback(true);        MyBatisGenerator myBatisGenerator = new MyBatisGenerator(config, callback, warnings);        myBatisGenerator.generate(null);        System.out.println("代码生成完毕>>>>>>>>>>>>");    }}