package com.designPdatterns.strategy.three;import java.util.HashMap;import java.util.Map;public class DiscountStrategyFactory {    private static final Map<String,DiscountStrategy> strategyMap = new HashMap<>();    static {        strategyMap.put("NORMAL",new NormalDiscountStrategy());        strategyMap.put("GROUPON",new GrouponDiscountStrategy());        strategyMap.put("PROMOTION",new PromotionDiscountStrategy());    }    public static DiscountStrategy getDiscountStrategy(String orderType){        if (orderType == null)throw new IllegalArgumentException("参数异常");        return strategyMap.get(orderType);    }}