package com.designPdatterns.strategy.two;import java.util.HashMap;import java.util.Map;public class EvictionStrategyFactory {    private static final Map<String,EvictionStrategy> map = new HashMap<>();    static {        map.put("Fifo",new FifoEvictionStrategy());        map.put("Lfu",new LfuEvictionStrategy());        map.put("Lru",new LruEvictionStrategy());    }    public EvictionStrategy getEvictionStrategy(String type){        if (type == null) throw new IllegalArgumentException("无效参数");        return map.get(type);    }}