package com.designPdatterns.template.three;public abstract class BeiPiaoLife {    protected String breakfast;    protected String lunch;    protected String dinner;    final void oneDay(String name){        System.out.println(name+" 的一天开始了：");        qichuang(name);        breakfast(breakfast);        amWork(name);        lunch(lunch);        pmWork(name);        dinner(dinner);        System.out.println(name+" 的一天结束了：");        System.out.println("-----------------");    }    void qichuang(String name){        System.out.println(name+" 起床了");    }    abstract void breakfast(String breakfast);    void amWork(String name){        System.out.println(name+" 上午疯狂工作中");    }    abstract void lunch(String lunch);    void pmWork(String name){        System.out.println(name+" 下午疯狂工作中");    }    abstract void dinner(String dinner);}