package com.mainshi.proxy;//https://www.cnblogs.com/leifei/p/8263448.htmlpublic class Test {    public static void main(String[] args) {        staticProxy();        System.out.println();        jdkProxy();        System.out.println();        cglibProxy();    }    private static void cglibProxy() {        CglibProxy cglibProxy = new CglibProxy();        Developer instance = (Developer) cglibProxy.getInstance(new JavaDeveloper());        instance.code();        instance.debug();    }    private static void jdkProxy() {        JDKProxy jdkProxy = new JDKProxy();        Developer instance = (Developer) jdkProxy.getInstance(new JavaDeveloper());        instance.debug();        instance.code();    }    private static void staticProxy() {        StaticProxy developer = new StaticProxy(new JavaDeveloper());        developer.code();        developer.debug();    }}