package com.springAnnotation.beanlifecycle;import javax.annotation.PostConstruct;import javax.annotation.PreDestroy;public class Dog {    public Dog() {        System.out.println("Dog construct method");    }    @PostConstruct    public void init(){        System.out.println("Dog init @PostConstruct");    }    @PreDestroy    public void destroy(){        System.out.println("Dog destroy @PreDestroy");    }}