package com.springAnnotation.beanlifecycle;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.Import;@Configuration@Import(MyBeanPostProcessor.class)public class MainConfigOfCar {    @Bean(initMethod = "init",destroyMethod = "destroy")    public Car car(){        return new Car();    }    @Bean    public Cat cat(){        return new Cat();    }    @Bean    public Dog dog(){        return new Dog();    }}