package com.xiaohui.chapterone;import java.util.HashMap;import java.util.Map;public class OneTest {    public static void main(String[] args) {        fun4(5);    }    public static void fun4(int n){        if (n<=1){            System.out.println("我是最后执行的");            return;        }        System.out.println("我在递归");        fun4(n-1);    }    public static int three(){        Map map = new HashMap<Integer,Integer>();        int[] arrays = {3,1,2,5,4,9,7,2};        for (int i=0;i<arrays.length;i++){            if (map.put(arrays[i],arrays[i])!=null){                return arrays[i];            }        }        return 0;    }    public static int two(){        int[] arrays = {3,1,2,5,4,9,7,2};        for (int i=0;i<arrays.length;i++){            System.out.println("开始对比："+arrays[i]);            for (int j=0;j<i;j++) {                if (arrays[i]==arrays[j]) return arrays[i];            }        }        return 0;    }    public static void one(){        int result=0;        for (int i=1;i<10001;i++){            result+=i;        }        System.out.println(result);    }}