package com.example;import com.mybatis.dao.PersonDAO;import com.mybatis.pojo.Person;import org.apache.ibatis.io.Resources;import org.apache.ibatis.session.SqlSession;import org.apache.ibatis.session.SqlSessionFactory;import org.apache.ibatis.session.SqlSessionFactoryBuilder;import org.junit.Test;import org.junit.runner.RunWith;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.boot.test.context.SpringBootTest;import org.springframework.test.context.junit4.SpringRunner;import java.io.IOException;import java.io.InputStream;@SpringBootTest@RunWith(SpringRunner.class)public class TestOne {    @Autowired    private SqlSessionFactory sqlSessionFactory;    @Test    public void test1() throws Exception {        String path = "config/mybatis-config.xml";        InputStream resourceAsStream = Resources.getResourceAsStream(path);        SqlSessionFactory build = new SqlSessionFactoryBuilder().build(resourceAsStream);        SqlSession sqlSession = build.openSession();        PersonDAO mapper = sqlSession.getMapper(PersonDAO.class);        Person person = mapper.selectByPrimaryKey(1);        System.out.println(person);    }    @Test    public void test() {        SqlSession sqlSession = sqlSessionFactory.openSession();        PersonDAO mapper = sqlSession.getMapper(PersonDAO.class);        Person person = mapper.selectByPrimaryKey(1);        System.out.println(person);    }}